CREATE TABLE filme ( 
cod_film int not null auto_increment primary KEY, 
nome_film varchar(200),
film_year int,
runtime int,
imdb_link varchar(40),
vote_avg decimal(3,1),
overview varchar(1000),
vote_count int,
release_date date,
tagline varchar(1000),
collection varchar(200),
title varchar(200),
original_title varchar(200)
); 

select * from filme;


